DROP TABLE admin_info;
CREATE TABLE admin_info (id int NOT NULL AUTO_INCREMENT, admin_name varchar(50) NOT NULL, pass varchar(100) NOT NULL, admin_realname varchar(50) NOT NULL COMMENT '真实姓名', phone varchar(17) NOT NULL COMMENT '手机号', add_time varchar(50) NOT NULL COMMENT '注册日期', belong_role int(2) DEFAULT '0' NOT NULL COMMENT '0-普通角色，1-管理员', status int(2) DEFAULT '0' NOT NULL COMMENT '0-启用，1-停用,2-冻结', userno varchar(50) NOT NULL COMMENT '用户编号', login_ip varchar(255) COMMENT '登录ip', login_time varchar(255) COMMENT '登录时间', enterprise_no varchar(255) COMMENT '所属企业编号', belong_platform int(2) COMMENT '0-企业,1-总后台,2机构，3.银行', error_count int DEFAULT '0' COMMENT '登陆错误次数', PRIMARY KEY (id)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='用户表';
DROP TABLE back_carte;
CREATE TABLE back_carte (id int NOT NULL AUTO_INCREMENT, carte_name varchar(50) NOT NULL COMMENT '菜单名称', parents varchar(100) NOT NULL COMMENT '父菜单标识', carte_intro varchar(100) COMMENT '菜单介绍', levels int(2) DEFAULT '0' NOT NULL COMMENT '菜单等级', target varchar(50) NOT NULL COMMENT '打开方式', rel varchar(100) NOT NULL COMMENT 'REL属性', href varchar(100) NOT NULL COMMENT 'URL地址', parent_id varchar(100) NOT NULL COMMENT '菜单标识', belong_platform int(2) COMMENT '0-企业端,1-总后台,2银行端', PRIMARY KEY (id)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='菜单表';
DROP TABLE baseinfo_key;
CREATE TABLE baseinfo_key (id int NOT NULL AUTO_INCREMENT, key_en varchar(50) NOT NULL COMMENT 'KEY的英文标识', key_name varchar(100) NOT NULL COMMENT 'KEY名称', remark varchar(100) COMMENT '备注', addname varchar(50) NOT NULL COMMENT '添加人', addtime varchar(50) NOT NULL COMMENT '添加时间', vl_num int(10) DEFAULT '0' NOT NULL COMMENT '值个数(0-无，1-一个，2-多个)', key_value varchar(50) COMMENT '值(内容)', parent_key_en varchar(50) COMMENT '父类KEY标识', parent_key_name varchar(50) COMMENT '父类KEY的名称', status int(2) DEFAULT '0' NOT NULL COMMENT '0-启用，1-禁用', PRIMARY KEY (id)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='基础数据(KEY)';
DROP TABLE baseinfo_value;
CREATE TABLE baseinfo_value (id int NOT NULL AUTO_INCREMENT, value_en varchar(50) COMMENT '英文标识', value_name varchar(50) COMMENT '名称', value_content varchar(50) DEFAULT '0' NOT NULL COMMENT '内容', key_en varchar(100) NOT NULL COMMENT '所属类型', addname varchar(50) COMMENT '添加人', PRIMARY KEY (id)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='基础数据VALUE表';
DROP TABLE login_record;
CREATE TABLE login_record (id int NOT NULL AUTO_INCREMENT COMMENT '编号', user_name varchar(50) COMMENT '登录名', real_name varchar(50) COMMENT '姓名', login_date date COMMENT '登录日期', login_time time COMMENT '登录时间', login_ip varchar(50) COMMENT '登录ip', login_state int COMMENT '登录状态0成功1失败', note int COMMENT '内容0成功1密码错误2验证码错误', enterprise_no varchar(255) COMMENT '所属企业编号', PRIMARY KEY (id)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='登录记录表';
DROP TABLE permission;
CREATE TABLE permission (id int(10) NOT NULL AUTO_INCREMENT COMMENT '主键', action_class varchar(100) NOT NULL COMMENT '类名', lower_href varchar(100) NOT NULL COMMENT '表单地址', status int(2) DEFAULT '0' NOT NULL COMMENT '状态(0-启用，1-禁用)', behavior_args varchar(100) COMMENT '行为模板', behavior varchar(50) COMMENT '行为名称', carte_id int(2) DEFAULT '0' NOT NULL COMMENT '所属菜单', button_name varchar(50) NOT NULL COMMENT '按钮名称', href varchar(100) NOT NULL COMMENT '按钮路径', action_method varchar(50) NOT NULL COMMENT '方法名称', create_time varchar(50) COMMENT '添加时间', PRIMARY KEY (id)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='按钮表';
DROP TABLE permission_mapping;
CREATE TABLE permission_mapping (id int NOT NULL AUTO_INCREMENT COMMENT '主键', admin_id int(10) DEFAULT '0' NOT NULL COMMENT '用户ID', per_id int(10) DEFAULT '0' NOT NULL COMMENT '按钮ID', PRIMARY KEY (id)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='按钮分配';
DROP TABLE system_notification;
CREATE TABLE system_notification (id int NOT NULL AUTO_INCREMENT COMMENT '编号', theme varchar(255) COMMENT '标题', note varchar(500) COMMENT '内容', create_time datetime COMMENT '发布时间', add_man varchar(255) COMMENT '发布人', PRIMARY KEY (id)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='系统通知表';
DROP TABLE user_allot;
CREATE TABLE user_allot (id int NOT NULL AUTO_INCREMENT COMMENT '主键', carte_id int(10) DEFAULT '0' NOT NULL COMMENT '菜单ID', admin_id int(10) DEFAULT '0' NOT NULL COMMENT '用户ID', PRIMARY KEY (id)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='用户菜单表';
